def add(number1: float, number2: float) -> float:
    """
    Function adds two numbers and return sum of two numbers.
    Args:
        number1 (float): 1st float number
        number2 (float): 2nd float number
    Returns:
        float: Sum of number1 and number2
    """
    return number1+number2


def subtract(number1: float, number2: float) -> float:
    """
    Function subtracts two numbers and return difference of two numbers.
    Args:
        number1 (float): 1st float number
        number2 (float): 2nd float number
    Returns:
        float: Difference of number1 and number2
    """
    return number1-number2
