def xyxy2xywh(bboxes):
    new_bboxes = np.zeros(bboxes.shape)
    new_bboxes[:,0] = bboxes[:,0]
    new_bboxes[:,1] = bboxes[:,1]
    new_bboxes[:,2] = bboxes[:,2]-bboxes[:,0]
    new_bboxes[:,3] = bboxes[:,3]-bboxes[:,1]
    return new_bboxes

def labels2coco(labels,new_cls):
    new_labels=[new_cls.index(COCO_CATEGORY[int(lb)]) for lb in labels]
    return np.array(new_labels)

